import React from 'react';
class ProfileStatus extends React.Component {
state = {
    editMode:false
}

activeEditMode(){
    this.setState({
        editMode:true
    });
}
render(){
    return(
        <>
        {this.editMode?
        <div>
            <input value={this.props.status}></input>
        </div>
        :
        <div>
            <span onDoubleClick={this.activeEditMode(true).bind(this)}>{this.props.status}</span>
        </div>
        }
        </>
    )
}
}

export default ProfileStatus
