import Profile from "./Profile";
import React from "react"
import * as axios from "axios"
import { userAPI } from "./api/api";
import { Redirect, withRouter } from "react-router-dom"
import { WithAuthRedirect } from "./hoc/WithAuthRedirect";
import { connect } from "react-redux";
import { changeProfileInfo, handleAddPostActionCreate, handleChangeMessageActionCreate, getUserProfileThunkCreator } from "./profileReducer";

const KEY = "b1186a4f-cbbb-4035-a7df-e7b360e29e9f"

class ProfileContainerAPI extends React.Component {

  componentDidMount() {

    let userId = this.props.match.params.userId
    if (!userId) {
      userId = 2
    }
    this.props.getUserProfileThunkCreator(userId)
  }
  render() {
    
    return (
      <Profile {...this.props} />
    )
  }
}

let mapStateToPropsForRedirect = (state) => {
  return {
    isAuth:state.auth.isAuth
  }
}

export let AuthRedirectComponent = WithAuthRedirect(ProfileContainerAPI)
AuthRedirectComponent = connect(mapStateToPropsForRedirect)

let mapStateToProps = (state) => {
  return {
    profilePage: state.profilePage,
  }
}
let WithUrlContainerContainer = withRouter(AuthRedirectComponent)

const ProfileContainer = connect(mapStateToProps, {
  updateText: handleChangeMessageActionCreate,
  addPost: handleAddPostActionCreate,
  setUserProfile: changeProfileInfo,
  getUserProfileThunkCreator
})(WithUrlContainerContainer)


export default ProfileContainer;
